document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".fab-action").forEach(t=>{t.addEventListener("click",function(n){let e=n.currentTarget.dataset.action;document.dispatchEvent(new CustomEvent("fab:action",{detail:{action:e}}))})})});window.Alpine&&window.Alpine.data("fabActions",()=>({init(){},handleAction(t){document.dispatchEvent(new CustomEvent("fab:action",{detail:{action:t}}))}}));
